import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

  function Square(props) {
    return (
      <button className="square" onClick={props.onClick} style={{backgroundColor:props.bgColor}} >

        {/* {props.value} */}
      </button>
    );
  }

//   function Play(props) {
//     return (
//       <button className="play" onClick={props.runSimulation}>
//         {props.value}
//       </button>
//     );
//   }

class StopButton extends React.Component {
    handleClick() {
      console.log('starting simulation');
    }

    render() {
      // This syntax ensures `this` is bound within handleClick
      return (
        <button onClick={(e) => this.handleClick(e)}>
          ■ Stop
        </button>
      );
    }
  }

class PlayButton extends React.Component {
    handleClick() {
      console.log('starting simulation');
    }

    render() {
      // This syntax ensures `this` is bound within handleClick
      return (
        <button onClick={(e) => this.handleClick(e)}>
          ▶ Play
        </button>
      );
    }
  }

  class Board extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        squares: Array(400).fill('white'),
        xIsNext: true,
        simulationRunning: false,
        generation: 0,
      };
    }

    handleClick(i) {
      const squares = this.state.squares.slice();
      if (this.state.simulationRunning) {
        return;
      }
      squares[i] = this.state.xIsNext ? 'white' : 'black';
      this.setState({
        squares: squares,
        xIsNext: !this.state.xIsNext,
      });
    }

    renderSquare(i) {
      return (
        <Square
          bgColor={this.state.squares[i]}
          onClick={() => this.handleClick(i)}
        />
      );
    }

    render() {
      let status;
      if (this.state.simulationRunning) {
        this.setState({generation: this.state.generation+1});
      } 
      status = 'Generation: ' + this.state.generation;

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
            {this.renderSquare(3)}
            {this.renderSquare(4)}
          </div>
          <div className="board-row">
            {this.renderSquare(5)}
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
            {this.renderSquare(9)}
          </div>
          <div className="board-row">
            {this.renderSquare(10)}
            {this.renderSquare(11)}
            {this.renderSquare(12)}
            {this.renderSquare(13)}
            {this.renderSquare(14)}
          </div>
          <div className="board-row">
            {this.renderSquare(15)}
            {this.renderSquare(16)}
            {this.renderSquare(17)}
            {this.renderSquare(18)}
            {this.renderSquare(19)}
          </div>
          <div className="board-row">
            {this.renderSquare(20)}
            {this.renderSquare(21)}
            {this.renderSquare(22)}
            {this.renderSquare(23)}
            {this.renderSquare(24)}
          </div>
        </div>
      );
    }
  }

  class Simulation extends React.Component {
    render() {
      return (
        <div className="sim">
          <div className="sim-board">
            <Board />
          </div>
          <div className="sim-controls" >
                 <PlayButton /><StopButton />
          </div>
          <div className="sim-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Simulation />,
    document.getElementById('root')
  );
