import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

var simulationRunning = false;

  function Square(props) {
    return (
      <button className="square" onClick={props.onClick} style={{backgroundColor:props.bgColor}} >

        {/* {props.value} */}

      </button>
    );
  }

//   function Play(props) {
//     return (
//       <button className="play" onClick={props.runSimulation}>
//         {props.value}
//       </button>
//     );
//   }

class StopButton extends React.Component {
    handleClick() {
      console.log('stopping simulation');
      simulationRunning = false;
    }

    render() {
      // This syntax ensures `this` is bound within handleClick
      return (
        <button onClick={(e) => this.handleClick(e)}>
          ■ Stop
        </button>
      );
    }
  }

class PlayButton extends React.Component {
    handleClick() {
      console.log('starting simulation');
      simulationRunning = true;
    }

    render() {
      // This syntax ensures `this` is bound within handleClick
      return (
        <button onClick={(e) => this.handleClick(e)}>
          ▶ Play
        </button>
      );
    }
  }

  class Board extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        squares: Array(400).fill('white'),
        simulationRunning: false,
        generation: 0,
        size: 20,
      };
    }

    buildNextGen() {
        var newSquares = this.state.squares;
        for(var i=0; i<newSquares.length; i++ ) {
            var liveNeighbors = 0;
            var curStatus = this.state.squares[i];

            // check 8 neighbors in squares, count black (alive)

            if( curStatus === 'black' ) // is alive, will neighbors kill it?
            {
                if( liveNeighbors === 2 || liveNeighbors === 3 )
                {
                    newSquares[i] = 'black'; // staying alive!
                }
                else
                {
                    newSquares[i] = 'white'; // death by overcrowding or underpopulation
                }
            }
            else  // is dead, is situation right for birth?
            {
                if( liveNeighbors === 3 )
                {
                    newSquares[i] = 'black'; // yes
                }
                else
                {
                    newSquares[i] = 'white'; // no
                }
            }
        }
        // wait ??? seconds

        // set Board's squares to newSquares
        this.setState({
            squares: newSquares,
            generation: this.state.generation + 1,
          });
    }

    handleClick(i) {
      const squares = this.state.squares.slice();
      if (this.state.simulationRunning) {
        return;
      }
      if( squares[i] === 'white') {
          squares[i] = 'black';
      }
      else {
          squares[i] = 'white';
      }
      this.setState({
        squares: squares,
      });
    }

    renderSquare(i) {
      return (
        <Square
          bgColor={this.state.squares[i]}
          onClick={() => this.handleClick(i)}
        />
      );
    }

    render() {
      let status;
      while (simulationRunning) {
        this.buildNextGen();
      } 
      status = 'Generation: ' + this.state.generation;

      return (
          <div className="top">
                <div className="status">{status}</div>

                <div className="board-row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                </div>
                <div className="board-row">
                    {this.renderSquare(5)}
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                    {this.renderSquare(9)}
                </div>
                <div className="board-row">
                    {this.renderSquare(10)}
                    {this.renderSquare(11)}
                    {this.renderSquare(12)}
                    {this.renderSquare(13)}
                    {this.renderSquare(14)}
                </div>
                <div className="board-row">
                    {this.renderSquare(15)}
                    {this.renderSquare(16)}
                    {this.renderSquare(17)}
                    {this.renderSquare(18)}
                    {this.renderSquare(19)}
                </div>
                <div className="board-row">
                    {this.renderSquare(20)}
                    {this.renderSquare(21)}
                    {this.renderSquare(22)}
                    {this.renderSquare(23)}
                    {this.renderSquare(24)}
                </div>

            </div>
      );
    }
  }

  class Simulation extends React.Component {
    render() {
      return (
        <div className="sim">
          <div className="sim-board">
            <Board /> 

          </div>
          <div className="sim-controls" >
            <PlayButton /><StopButton />
          </div>
          <div className="sim-about">
                About this Algorithm...
            </div>
          <div className="sim-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Simulation />,
    document.getElementById('root')
  );
